        console.log('Enviando...');
    }

    return (
        <Fragment>
            <h2 className="text-center">Crear Nueva Cita</h2>
            <Form
                onSubmit={submitPetDate}
            >
                <Form.Row>
                    <Form.Group as={Col}>
                        <Form.Label>Nombre mascota</Form.Label>
                        <Form.Control 
                            type="text" 
                            placeholder="Nombre mascota"
                            name="pet"
                            value={pet}
                            onChange={handleState}
                        />
                    </Form.Group>
                </Form.Row>
                <Form.Group>
                    <Form.Label>Dueño</Form.Label>
                    <Form.Control 
                        type="text" 
                        placeholder="Dueño"
                        name="petOwner"
                        pet={petOwner}
                        onChange={handleState}
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Fecha</Form.Label>
                    <Form.Control 
                        type="date"
                        name="date"
                        value={date}
                        onChange={handleState}
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Dueño</Form.Label>
                    <Form.Control 
                        type="time"
                        name="hour"
                        value={hour}
                        onChange={handleState}
                    />
                </Form.Group>
                <Form.Group>
                    <Form.Label>Síntomas</Form.Label>
                    <Form.Control 
                        as="textarea" 
                        rows={5}
                        name="symptoms"
                        value={symptoms}
                        onChange={handleState}
                    />
                </Form.Group>
                <Button
                    type="submit"
                    variant="info"
                    className="btn-block"
                >Enviar</Button>
            </Form>
        </Fragment>
    );
}

export default Formdate;
