import React, { Fragment, useState } from 'react';
import Formdate from './Formdate';
import Date from './Date';

import '../css/App.css';
import {Container, Row, Col} from 'react-bootstrap';

function App() {
  //array state of petdates
  const [petDates, savePetDates] = useState([]);

  //Function that takes actual dates and add a new date
  const createPetDate = petDate => {
    console.log(petDate);
    savePetDates([
      ...petDates,
      petDate
    ]);
  }

  return (
    <Fragment>
      <h1 className="text-center text-info text-uppercase py-5">Administrador de Pacientes</h1>
      <Container>
        <Row>
          <Col xs={12} md={6}>
            <Formdate
              createPetDate={createPetDate}
            />
          </Col>
          <Col xs={12} md={6}>
            {petDates.map(petDate => (
              <Date
                key={petDate.id}
                date={petDate}
              />
            ))}
          </Col>
        </Row>
      </Container>
    </Fragment>
  );
}

export default App;
